package EjerciciosPropuestos1;

public class Ej2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=1,y=2;
		double n=4.7, m=3.2;
		
		System.out.println("Variable x= " +x);
		System.out.println("Variable x= " +y);
		System.out.println("Variable x= " +n);
		System.out.println("Variable x= " +m);
		
		System.out.println("La suma " +(x+y));
		System.out.println("La diferencia " +(x-y));
		System.out.println("El producto " +(x*y));
		System.out.println("La suma " +(x/y));
		System.out.println("La suma " +(x%y));
		
		System.out.println("La suma " +(n+m));
		System.out.println("La diferencia " +(n-m));
		System.out.println("El producto " +(n*m));
		System.out.println("La suma " +(n/m));
		System.out.println("La suma " +(n%m));
		
		System.out.println("La suma " +(x+n));
		System.out.println("La diferencia " +(y/m));
		System.out.println("El producto " +(y%m));
		
		System.out.println("El doble de x " +(n*2));
		System.out.println("El doble de x " +(m*2));
		System.out.println("El doble de x " +(x*2));
		System.out.println("El doble de x " +(y*2));
		System.out.println("La suma de todas las variables " +(n+m+x+y));
		System.out.println("El producto de todas las variables " +(n*m*x*y));
		
		

	}

}
