package programacion;

public class aritmeticos1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//varibles
		
		int num1=2,num2;
		//producto
		num2=num1*num1; //num2=4
		System.out.println("num1 vale: "+num1+" y num2 vale: "+num2);
		
		num2=num2-num1; //num2=2
		System.out.println("num1 vale: "+num1+" y num2 vale: "+num2);

		num2=num2+num1+15; //num2=19
		System.out.println("num1 vale: "+num1+" y num2 vale: "+num2);
		
		num2=num2%num1; //num2=1
		System.out.println("num1 vale: "+num1+" y num2 vale: "+num2);
		
	}

}
