package programacion;

public class relacionales {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int m=2, n=5;
		boolean res;
		res = (m > n);//res=false
		System.out.println(res);
		res = (m < n);//res=true
		System.out.println(res);
		res = (m >= n);//res=false
		System.out.println(res);
		res = (m <= n);//res=true
		System.out.println(res);
		res = (m == n);//res=false
		System.out.println(res);
		res = (m != n);//res=true
	
		

	}

}
